# Define the target word to search for
target_word = "XMAS"
word_len = len(target_word)

# Convert the input string into a list of strings (grid)
grid = input_data.strip().splitlines()

# Get the number of rows and columns in the grid
rows = len(grid)
cols = len(grid[0])

# Function to check for the word "XMAS" in all directions
def count_xmas(grid, target_word):
    count = 0
    
    # Directions: right (0, 1), left (0, -1), down (1, 0), up (-1, 0)
    directions = [
        (0, 1),   # Right
        (0, -1),  # Left
        (1, 0),   # Down
        (-1, 0),  # Up
        (1, 1),   # Down-right (diagonal)
        (-1, -1), # Up-left (diagonal)
        (1, -1),  # Down-left (diagonal)
        (-1, 1),  # Up-right (diagonal)
    ]
    
    # Function to check if the word fits in a given direction
    def is_word_at(r, c, dr, dc):
        for i in range(word_len):
            nr, nc = r + dr * i, c + dc * i
            if nr < 0 or nr >= rows or nc < 0 or nc >= cols or grid[nr][nc] != target_word[i]:
                return False
        return True
    
    # Loop over each cell in the grid
    for r in range(rows):
        for c in range(cols):
            # For each direction, check if the word "XMAS" fits starting from (r, c)
            for dr, dc in directions:
                if is_word_at(r, c, dr, dc):
                    count += 1

    return count

# Run the function and print the result
xmas_count = count_xmas(grid, target_word)
print(f"XMAS appears {xmas_count} times in the grid.")

