def count_x_mas_patterns(grid):
    rows, cols = len(grid), len(grid[0])
    pattern_count = 0

    # Check for X-MAS patterns in the grid
    for r in range(1, rows - 1):  # Skip top and bottom rows
        for c in range(1, cols - 1):  # Skip leftmost and rightmost columns
            # Check for the X-MAS pattern centered at (r, c)
            if (
                # Top-left to bottom-right diagonal (MAS or SAM)
                ((grid[r - 1][c - 1] == "M" and grid[r + 1][c + 1] == "S") or
                 (grid[r - 1][c - 1] == "S" and grid[r + 1][c + 1] == "M")) and
                # Top-right to bottom-left diagonal (MAS or SAM)
                ((grid[r - 1][c + 1] == "M" and grid[r + 1][c - 1] == "S") or
                 (grid[r - 1][c + 1] == "S" and grid[r + 1][c - 1] == "M")) and
                # Center is "A"
                grid[r][c] == "A"
            ):
                pattern_count += 1

    return pattern_count

